function 动画(控件,时间)
  旋转动画=RotateAnimation(0, 360,
  Animation.RELATIVE_TO_SELF, 0.5,
  Animation.RELATIVE_TO_SELF, 0.5)
  旋转动画.setDuration(时间)--设置动画时间
  旋转动画.setInterpolator(AccelerateDecelerateInterpolator())
  旋转动画.setFillAfter(true)--设置动画结束后停留位置
  旋转动画.setRepeatCount(-1)--设置无限循环
  --绑定动画
  控件.startAnimation(旋转动画)
end
task(15,function()
  动画(cd1,1500)
  task(15,function()
    动画(cd2,1500)
    task(15,function()
      动画(cd3,1500)
      task(15,function()
      动画(cd4,1500)
      task(15,function()
      动画(cd5,1500)
      task(15,function()
      动画(cd6,1500)
      return true
    end)
      return true
    end)
      return true
    end)
      return true
    end)
    return true
  end)
  return true
end)

