shamrock=
--布局框架
{
  LinearLayout;--线性布局
  Orientation='vertical';--布局方向
  layout_width='fill';--布局宽度
  layout_height='fill';--布局高度
  background='#ffffff';--布局背景颜色(或者图片路径)
  {
    --登录总界面
    CardView;--卡片控件
    layout_margin="5%w";--设置边距
    layout_gravity='center';--重力属性
    Elevation='5';--阴影属性
    layout_width='fill';--卡片宽度
    layout_height='fill';--卡片高度
    radius='30';--卡片圆角
    CardBackgroundColor='#ffffff';--卡片背景颜色
    {
      --登陆总布局
      LinearLayout;--线性布局
      Orientation='vertical';--布局方向
      layout_width='fill';--布局宽度
      layout_height='fill';--布局高度
      {
        --上半部分布局
        LinearLayout;--线性布局
        Orientation='vertical';--布局方向
        layout_width='fill';--布局宽度
        layout_weight='6';--布局权重
        background='#FF4FB3AE';--布局背景颜色(或者图片路径)
        Gravity="center";--设置居中
        {
          TextView;--文本控件
          layout_width='wrap';--文本宽度
          layout_height='wrap';--文本高度
          Gravity='center';--重力属性
          textColor='#ffffff';--文本颜色
          text='Welcome';--显示的文本
          textSize='50sp';--文本大小
          padding="5%w";--设置内边距
        };
        {
          TextView;--文本控件
